import React, { useEffect } from 'react'

import MetaData from '../layout/MetaData'
import CheckoutSteps from './CheckoutSteps'

import { createOrder, clearErrors } from '../../actions/orderActions'

import { useAlert } from 'react-alert'
import { useDispatch, useSelector } from 'react-redux'

import  { 
  useStripe,
  useElements, 
  CardNumberElement, 
  CardExpiryElement, 
  CardCvcElement
} from '@stripe/react-stripe-js'

import axios from 'axios'
//Payment Info
const options = {
  style: {
    base: {
      fontSize: '16px',
    },
    invalid: {
      color: '#9e2146'
    }
  }
}

const Payment = ({ history }) => {
  console.log("Here")

  const alert = useAlert();
  const stripe = useStripe();
  const elements = useElements();
  const dispatch = useDispatch();

  const { user } = useSelector(state => state.auth);
  const { cartItems, shippingInfo } = useSelector(state => state.cart);
  const { error } = useSelector(state => state.newOrder)

  useEffect(() => {

    if (error) {
      alert.error(error);
      dispatch(clearErrors());
    }

  }, [dispatch, alert, error]);

  const order = {
    orderItems: cartItems,
    shippingInfo
  }

  const orderInfo = JSON.parse(sessionStorage.getItem('orderInfo'));
  if (orderInfo) {
    order.itemsPrice = orderInfo.itemsPrice
    order.shippingPrice = orderInfo.shippingPrice
    order.taxPrice = orderInfo.taxPrice
    order.totalPrice = orderInfo.totalPrice
  }

  const paymentData = {
    amount: Math.round(orderInfo.totalPrice * 100)
  }

  const submitHandler= async (e) => {
    e.preventDefault();

    document.querySelector('#pay_btn').disabled = true;

    let res;

    try {

      const config = {
        headers: {
          'Content-Type': 'application/json'
        }
      }

      res = await axios.post('/api/v1/payment/process', paymentData, config);

      const clientSecret = res.data.client_secret;

      if (!stripe || !elements) return;

      const result = await stripe.confirmCardPayment(clientSecret, {
        payment_method: {
          card: elements.getElement(CardNumberElement),
          billing_details: {
            name: user.name,
            email: user.email
          }
        }
      });

      if (result.error) {
        alert.error(result.error.message);
        document.querySelector('#pay_btn').disabled = false;

      } else {
        // The payment is proccessed or not
        if (result.paymentIntent.status === 'succeeded') {

          order.paymentInfo = {
            id: result.paymentIntent.id,
            status: result.paymentIntent.status
          }

          dispatch(createOrder(order));
          history.push('/success');
        } else {
          alert.error('There is some issue while processing payment.');
        }
      }

    } catch(err) {
      document.querySelector('#pay_btn').disabled = false;
      alert.error(err.response.data.errorMessage);
    }
  }
//Card
  return (
    <>
      <MetaData title={'Payment Details'} />

      <CheckoutSteps shipping confirmOrder payment />

      <div className="row wrapper">
		    <div className="col-10 col-lg-5">

          <form className="shadow-lg" onSubmit={submitHandler}>
            <h1 className="mb-4">Card Info</h1>
            <div className="form-group">
              <label htmlFor="card_num_field">Card Number</label>
              <CardNumberElement
                type="text"
                id="card_num_field"
                className="form-control"
                options={options}
              />
              <p className="text-muted font-weight-light">* Test card number: 4000 0027 6000 3184 </p>

            </div>

				    <div className="form-group">
              <label htmlFor="card_exp_field">Card Expiry</label>
              <CardExpiryElement
                type="text"
                id="card_exp_field"
                className="form-control"
                options={options}
              />
              <p className="text-muted font-weight-light">* 4 digits</p>
            </div>

				    <div className="form-group">
              <label htmlFor="card_cvc_field">Card CVC</label>
              <CardCvcElement
                type="text"
                id="card_cvc_field"
                className="form-control"
                options={options}
              />
              <p className="text-muted font-weight-light">* 3 digits</p>
            </div>


            <button
              id="pay_btn"
              type="submit"
              className="btn btn-block py-3"
            >
              Pay {`- ${orderInfo && orderInfo.totalPrice}`}
            </button>
          </form>

			  </div>
      </div>
    </>
  )
}

export default Payment
